import React, { Component } from 'react';

// component enfant
class Count extends Component {
  constructor() {
    super();
    this.state = {
      count: 0
    }
  }

  addOne() {
    this.setState({
      count: this.state.count + 1
    });
  }

  removeOne() {
    this.setState({
      count: this.state.count - 1
    });
  }

  render() {
    return (
      // Utilisation des props = Parent vers enfant
      <div>
        <p>Compteur : {this.state.count}</p>
        <button onClick={() => this.addOne()} >Augmenter</button>
        <button onClick={() => this.removeOne()} >Diminuer</button>
      </div>
    );
  }
}

export default Count;
